{	short indexCursor;	switch(cursorID)	{		case GLUT_CURSOR_RIGHT_ARROW:			indexCursor = 0;		break;		case GLUT_CURSOR_LEFT_ARROW:			indexCursor = 1;		break;		case GLUT_CURSOR_INFO:			indexCursor = 2;		break;		case GLUT_CURSOR_DESTROY:			indexCursor = 3;		break;		case GLUT_CURSOR_HELP:			indexCursor = 4;		break;		case GLUT_CURSOR_CYCLE:			indexCursor = 5;		break;		case GLUT_CURSOR_SPRAY:			indexCursor = 6;		break;		case GLUT_CURSOR_WAIT:			indexCursor = 7;		break;		case GLUT_CURSOR_TEXT:			indexCursor = 8;		break;		case GLUT_CURSOR_CROSSHAIR:			indexCursor = 9;		break;		case GLUT_CURSOR_UP_DOWN:			indexCursor = 10;		break;		case GLUT_CURSOR_LEFT_RIGHT:			indexCursor = 11;		break;		case GLUT_CURSOR_TOP_SIDE:			indexCursor = 12;		break;		case GLUT_CURSOR_BOTTOM_SIDE:			indexCursor = 13;		break;		case GLUT_CURSOR_LEFT_SIDE:			indexCursor = 14;		break;		case GLUT_CURSOR_RIGHT_SIDE:			indexCursor = 15;		break;		case GLUT_CURSOR_TOP_LEFT_CORNER:			indexCursor = 16;		break;		case GLUT_CURSOR_TOP_RIGHT_CORNER:			indexCursor = 17;		break;		case GLUT_CURSOR_BOTTOM_RIGHT_CORNER:			indexCursor = 18;		break;		case GLUT_CURSOR_BOTTOM_LEFT_CORNER:			indexCursor = 19;		break;		case GLUT_CURSOR_INHERIT:			indexCursor = 20;		break;		case GLUT_CURSOR_NONE:			indexCursor = 21;		break;		case GLUT_CURSOR_FULL_CROSSHAIR:			indexCursor = 22;		break;		default:			__glutWarning("glutSetCursor: cursor id out of range");		return;	}		if(!__glutCurrentWindow)	{		__glutWarning("glutSetCursor: no active window");		return;	}		if(!cursorTable[indexCursor])	{		CursHandle hCursor = GetCursor (BASE_CURSOR + indexCursor);		cursorTable[indexCursor] = hCursor;		if(cursorTable[indexCursor]) 			HLock((Handle) cursorTable[indexCursor]);	}		if(cursorTable[indexCursor])	{		SetCursor(*cursorTable[indexCursor]);		__glutCurrentWindow->cursor = cursorID;	}}